import React ,{useState} from 'react'
import 'codemirror/lib/codemirror.css'
import 'codemirror/theme/material.css'
import 'codemirror/mode/xml/xml'
import 'codemirror/mode/javascript/javascript'
import 'codemirror/mode/css/css'
import { Controlled as ControlledEditor } from 'react-codemirror2';

function Editor (props) {
    const[open,setOpen] = useState(true)
    function handleChange(editor,data,value)
    {
        props.onChange(value)
    }

  return (
    <div className={`grow basis-11 p-5 ${open ?'':'grow-0'}`}>
    {/* title part of the editor */}
      <div className='justify-between bg-black text-white p-5 rounded-t-lg '>
        {props.displayName}
        <button onClick={ () => {setOpen(!open)}}>O/C</button>
      </div>
    {/*Code Editing part of the editr */}
      <ControlledEditor
        className=' grow overflow-hidden h-9'
        onBeforeChange={handleChange}
        value={props.value}
        options={{
            lineWrapping: true,
            line: true,
            mode: props.language,
            theme:'material',
            lineNumbers: true
        }}
      />
    </div>
  )
}

export default Editor
